import React, { useState } from "react";
import Recommendation from "./Recommendation";
const QList = [
    {
        id:0,
        q:'원하는 단맛의 정도는? ',
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    },
    {
        id:1,
        q:'원하는 신맛의 정도는? ',
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    },
    {
        id:2,
        q:'원하는 바디감의 정도는? ',
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    },
    {
        id:3,
        q:'원하는 청량감의 정도는?' ,
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    }
]

function QuestionList(){
    const [answer, nextAnswer] = useState('');
    function next(index) {
        for(let i in QList(index).a){
            nextAnswer = QList(index).a[i].answer;
        }
        return answer;
    }
    const [index, setIndex] = useState(0);
    return(
        <div>
            <Recommendation 
                question = {QList[index].q}
                answer = {next(index)}/>
            <button onClick={index < 3 ? ()=>setIndex(index+1) : ()=>window.open(`/drinks/recommendation/result`,"_self")}>{index < 3 ? '다음' : '결과 보기!'}</button>
        </div>
    )

}
export default QuestionList;
