import React, { useState } from "react";
import Recommendation from "./Recommendation";
const QList = [
    {
        id:0,
        q:'원하는 단맛의 정도는? ',
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    },
    {
        id:1,
        q:'원하는 신맛의 정도는? ',
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    },
    {
        id:2,
        q:'원하는 바디감의 정도는? ',
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    },
    {
        id:3,
        q:'원하는 청량감의 정도는?' ,
        a:[
            {answer: 1},
            {answer: 2},
            {answer: 3},
            {answer: 4},
            {answer: 5},
        ]
    }
]
function QuestionList(){
    const [index, setIndex] = useState(0);
    return(
        <div>
            <Recommendation 
                question = {QList[index].q}
                answer = {QList[index].a}/>
            <button onClick={()=>setIndex(index+1)}>다음!</button>
        </div>
    )

}
export default QuestionList;
